    private void jButton1ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton1ActionPerformed
                
        jTextField2.setText("");
        jTextField3.setText("");
        jTextField4.setText("");
        jTextField5.setText("");
        jTextField6.setText("");
        jTextField7.setText("");
        jTextField8.setText("");
        jTextField9.setText("");
        jTextField10.setText("");
        jTextField11.setText("");
        jTextField12.setText("");
        jTextField13.setText("");
        jTextField14.setText("");
        jTextField15.setText("");
        jTextField16.setText("");
        
        
        
        
        
        
        
                String user = jTextField1.getText();
                char[] pas = jPasswordField1.getPassword(); 
                
                jTextField1.setText("");
                jPasswordField1.setText("");
                
                String pass = "";
                
                for (int i = 0; i < pas.length; i++) {
                    pass = pass + pas[i];
                }
                
                //System.out.println("pass - > " + pass);
                
                Controller ob = new Controller();
                boolean result = false;
                //System.out.println("Action");
        try {
            result = ob.login_check(user, pass);
        } catch (SQLException ex) {
            Logger.getLogger(ViewForm.class.getName()).log(Level.SEVERE, null, ex);
        }
                
                if (result) {
                    String type = "";
                    try {
                        type = ob.findtype(user, pass);
                    } catch (SQLException ex) {
                        Logger.getLogger(ViewForm.class.getName()).log(Level.SEVERE, null, ex);
                    }
                    
                    
                    if (type.equalsIgnoreCase("General user")) {
                        jLabel4.setText("Welcome " + type + " " + user);
                        jPanel2.setVisible(true);
                        jPanel1.setVisible(false);
                    } else if (type.equalsIgnoreCase("Admin")) {
                        jLabel7.setText("Welcome " + type + " " + user);
                        jPanel3.setVisible(true);
                        jPanel1.setVisible(false);
                    } else if (type.equalsIgnoreCase("Library personnel")) {
                        jLabel9.setText("Welcome " + type + " " + user);
                        jPanel4.setVisible(true);
                        jPanel1.setVisible(false);
                    }
                    
                    
                    
                }
                
                else jLabel6.setText("Unidentified Login");// TODO add your handling code here:
    }//GEN-LAST:event_jButton1ActionPerformed

