    private void jButton24ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton24ActionPerformed
         String bid, uid;
         
         bid = jTextField14.getText();
         uid = jTextField15.getText();
         
         Controller ob = new Controller();
         
         boolean flag = false;
        try {
            flag = ob.check_issue(bid);
        } catch (SQLException ex) {
            Logger.getLogger(ViewForm.class.getName()).log(Level.SEVERE, null, ex);
        }
         
         if (flag) {
             try {
                 ob.issue(bid, uid);
             } catch (SQLException ex) {
                 Logger.getLogger(ViewForm.class.getName()).log(Level.SEVERE, null, ex);
             }
             jLabel21.setText("Book Has Been Issued");
         } else {
             jLabel21.setText("Book Has Already Been Issued to Someone else");
         }
    }//GEN-LAST:event_jButton24ActionPerformed

